let holidaysForCountry = [];
let currentPage = 1; // Текущая страница
const holidaysPerPage = 5; // Количество праздников на странице

// Элементы с HTML страницы
const countrySelect = document.getElementById('countrySelect');
const selectCountryButton = document.getElementById('selectCountryButton');
const modal = document.getElementById('modal');
const closeButton = document.querySelector('.close-button');
const prevHolidayButton = document.getElementById('prevHolidayButton');
const nextHolidayButton = document.getElementById('nextHolidayButton');

// Заполняем список стран в селекте
const uniqueCountries = [...new Set(holidays.map(holiday => holiday.country))];
uniqueCountries.sort((a, b) => a.localeCompare(b));

function populateCountrySelect(countries) {
    countrySelect.innerHTML = ''; // Очищаем список
    countries.forEach(country => {
        const option = document.createElement('option');
        option.value = country;
        option.textContent = country;
        countrySelect.appendChild(option);
    });
}

// Инициализация списка стран
populateCountrySelect(uniqueCountries);

// Обработка нажатия кнопки "Показать праздники"
selectCountryButton.addEventListener('click', () => {
    const selectedCountry = countrySelect.value;
    if (selectedCountry) {
        renderHolidays(selectedCountry);
    }
});

// Функция для отображения праздников в выбранной стране
function renderHolidays(country) {
    holidaysForCountry = holidays.filter(holiday => holiday.country === country);
    holidaysForCountry.sort((a, b) => new Date(a.date) - new Date(b.date));

    currentPage = 1;

    updateHolidayList();
}

// Функция для обновления списка праздников
function updateHolidayList() {
    const countryList = document.getElementById('countryList');
    countryList.innerHTML = ''; // Очищаем список

    const start = (currentPage - 1) * holidaysPerPage;
    const end = start + holidaysPerPage;
    const holidaysToDisplay = holidaysForCountry.slice(start, end); // Срезаем массив

    holidaysToDisplay.forEach(holiday => {
        const holidayDate = new Date(holiday.date);
        const options = { month: 'long', day: 'numeric' };
        const formattedDate = holidayDate.toLocaleDateString(undefined, options);

        const button = document.createElement('button');
        button.textContent = `${holiday.name} (${formattedDate})`;
        button.addEventListener('click', () => {
            openModal(holiday);
        });
        countryList.appendChild(button);
    });

    countryList.style.display = 'block'; // Показываем список праздников
    document.querySelector('.navigation').style.display = 'block'; // Показываем навигацию

    // Управление отображением кнопок
    prevHolidayButton.style.display = (currentPage > 1) ? 'block' : 'none';
    nextHolidayButton.style.display = (currentPage < Math.ceil(holidaysForCountry.length / holidaysPerPage)) ? 'block' : 'none';
}

// Функция поиска стран
countrySelect.addEventListener('input', () => {
    const query = countrySelect.value.toLowerCase();
    const filteredCountries = uniqueCountries.filter(country => country.toLowerCase().startsWith(query));
    populateCountrySelect(filteredCountries);
});

// Открытие модального окна
function openModal(holiday) {
    document.getElementById('countryTitle').textContent = holiday.country;
    document.getElementById('holidayDescription').textContent = holiday.name;
    document.getElementById('holidayDetails').textContent = holidayDescriptions[holiday.name]?.description || '';
    document.getElementById('holidayImage').src = holidayDescriptions[holiday.name]?.image || '';
    document.getElementById('holidayImage').alt = holiday.name;

    modal.style.display = "block"; // Показываем модальное окно
}

// Закрытие модального окна
closeButton.addEventListener('click', closeModal);
window.addEventListener('click', (event) => {
    if (event.target === modal) {
        closeModal();
    }
});

function closeModal() {
    modal.style.display = "none"; // Скрываем модальное окно
}

// Обработчики для кнопок навигации
prevHolidayButton.addEventListener('click', () => {
    if (currentPage > 1) {
        currentPage--;
        updateHolidayList();
    }
});

nextHolidayButton.addEventListener('click', () => {
    if (currentPage < Math.ceil(holidaysForCountry.length / holidaysPerPage)) {
        currentPage++;
        updateHolidayList();
    }
});

// Инициализация
renderHolidays();
